import React,{useState} from 'react'
import {Link} from 'react-router-dom';
import arrow from './img/arrow1.png';
import './catagory.css';

const Catagory = () => {
   const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);

  };




  return (
    <div className='catagorys'>
        <div className="option">
          <Link className='cetegori'>Cetegories</Link> 
          <Link className="cetaoption">
            <div className="dropdown-toggle" onClick={toggleDropdown}>
              Art & Photography <img src={arrow} alt="" className='arrimg' />
            </div>
            {isDropdownOpen && (
              <div className="dropdown-menu">
                <Link className="cetaoption">Most Popular Writer</Link>
                   <div className='cataline'></div>
                <Link className="cetaoption">Service 2</Link>
                   <div className='cataline'></div>
                <Link className="cetaoption">Service 3</Link>

              </div>
            )}
          </Link>
          <div className='cataline'></div>
          <Link to='' className='cetaoption'>Biography <img src={arrow} alt="" className='arrimg' /> </Link>
          <div className='cataline'></div>
          <Link to='' className='cetaoption'>Children's Books <img src={arrow} alt="" className='arrimg'/> </Link>
          <div className='cataline'></div>
          <Link to='' className='cetaoption'>Croft & Hobbies <img src={arrow} alt="" className='arrimg' /> </Link>
        </div>

    </div>
  )
}

export default Catagory;
